public class Main {

 public static int maxFunction(int n1,int n2){

 int max;

 if(n1>n2)
 max=n1;
 else
 max=n2;
 return max;
 }
 public static void main(String[] args) {
 int a = 11;
 int b = 12 ;
 int c = maxFunction(a,b);

 System.out.println("Maximum Value = "+c);

 }

 }
